CREATE TABLE IF NOT EXISTS `{PREFIX}sticker_categories` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `icon` varchar(255) NOT NULL,
  `background_color` varchar(255) NOT NULL,
  `enabled` tinyint(1) DEFAULT '0',
  `ordering` int(11) DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `{PREFIX}sticker_sticker_images` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sticker_sticker_id` int(11) NOT NULL,
  `sticker_category_id` int(11) DEFAULT NULL,
  `filename` varchar(255) NOT NULL,
  `width` float NOT NULL,
  `height` float NOT NULL,
  `block` int(11) NOT NULL DEFAULT '1',
  `quantity` int(11) NOT NULL DEFAULT '1',
  `animation_interval` int(11) DEFAULT '0',
  `enabled` tinyint(1) NOT NULL DEFAULT '0',
  `ordering` int(11) DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `{PREFIX}sticker_stickers` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `icon` varchar(255) NOT NULL,
  `animation_interval` int(11) DEFAULT '0',
  `enabled` tinyint(1) NOT NULL DEFAULT '0',
  `ordering` int(11) DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS `{PREFIX}sticker_logs` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL,
  `stickers` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB;

